INSERT INTO `random` (`pkey`, `code`, `advname`, `minortxt`, `majortxt`, `minorimg`, `linktxt`, `linkurl`, `isvalid`, `dateposted`) VALUES (1,'5001','New marketing materials','Merrow has created a new stitchbook and other informational materials for dealers and customers.','Merrow has created a new stitchbook and other informational materials for dealers and customers.  To order copies of these materials, contact the marketing department: marketing@merrow.com','images/products/prodimg.gif','','',1,'2006-12-01'),(2,'5002','Merry Christmas','Best wishes from Merrow for the holiday season!','Merrow would like to wish all of our dealers and customers a Merry Christmas and a Happy New Year.','','','',1,'2006-12-01'),(3,'5003','MG3U Auto-Edge Machine','A completely automated machine for edging emblems.','The Merrow Optical Auto-Edge Machine (MG3U AEM) is the only machine in the world of its kind: a completely automated machine for edging emblems.  Loaded with unedged emblems, the MG3U AEM can run with minimal operator input, edging 350 to 600 emblems an hour, depending on emblem size.  This machine can be reloaded while the machine is running, allowing for uninterrupted productivity.  One operator can oversee up to four machines at a time, benefiting your plant with huge savings of time and money. A truly unique machine with unparalleled performance!','images/products/prodimg.gif','','',1,'2006-12-01');
